package com.zhengdianfang.dazhongbao.viewsimport android.content.Intentimport android.os.Bundleimport android.widget.Buttonimport com.zhengdianfang.dazhongbao.Rimport com.zhengdianfang.dazhongbao.presenters.StartupPresenterimport com.zhengdianfang.dazhongbao.views.basic.PresenterActivityimport com.zhengdianfang.dazhongbao.views.home.MainActivity/** * Created by dfgzheng on 25/07/2017. */class StartupActivity : PresenterActivity<StartupView, StartupPresenter, String>() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_startup)        val button = findViewById<Button>(R.id.button)        button.setOnClickListener {            startActivity(Intent(StartupActivity@this, MainActivity::class.java))        }    }    override fun onResume() {        super.onResume()    }    override fun createPresenter(): StartupPresenter? {       return StartupPresenter()    }}