package com.zhengdianfang.dazhongbao.viewsimport android.content.Intentimport android.os.Bundleimport com.zhengdianfang.dazhongbao.CApplicationimport com.zhengdianfang.dazhongbao.Rimport com.zhengdianfang.dazhongbao.views.basic.BaseActivityimport com.zhengdianfang.dazhongbao.views.home.MainActivityimport com.zhengdianfang.dazhongbao.views.login.LoginActivity/** * Created by dfgzheng on 25/07/2017. */class StartupActivity: BaseActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_startup)        if (CApplication.INSTANCE.isLogin()){            startActivity(Intent(this, MainActivity::class.java))        }else {            startActivity(Intent(this, LoginActivity::class.java))        }        finish()    }}